import React, { useState } from "react";
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';

const CreateNote =(props)=>
{

    const[expand,setExpand]=useState(false);
    const[note,setNote]=useState(
        {
            title:"",
            content:"",
        });

        const inputEvent =(event)=>
        {

            //const value= event.target.value;
            //const name= event.target.value;

            const{name,value}=event.target;

            setNote((prevData)=>
            {
                return{
                    ...prevData,
                [name]: value,


                };
            });
        };


        const addEvent=()=>
        {

            props.passNote(note);
            setNote(
                {
             title:"",
            content:"",
                }
            );
        };

const expandIt=()=>
{
    setExpand(true);
}

const btoNormal=()=>
{
    setExpand(false);
}

    return(
      <>
      <div className="main_note"  onDoubleClick={btoNormal}>
          <form>
          {expand?
              <input type="text"   value ={note.title} onChange={inputEvent} 
              name = "title" placeholder="Title" autoComplete="off"/>
              :null}

              <textarea rows='' columns='' value ={note.content}  onChange={inputEvent}
              name="content"  placeholder="Write a note..."
              onClick={expandIt}
             ></textarea>

              {expand?
              <Button onClick={addEvent}>
                   <AddIcon className="plus_sign"/>
              </Button>
              :null}

              
      
          </form>
          
      </div>
      <p style={{center}}>copyright Â© 2020</p>
      </>
    );
};

export default CreateNote;
