import React, { useState }  from 'react';

const Events =() =>{

    const purple ="#8e44ad";
    const[bg,setBg] =useState(purple);

    const[name,setName]=useState('Click Me');

    const bgChange =() =>
    {
       let newBg = "#34495e";
       setBg(newBg);
       setName("OUCH!!  ðŸ‘©");
        
    };

    const bgBack=()=>{
        setBg(purple);
        setName("AYYO!!  ðŸ‘¼");
    }

    return(
        <>
        <div style={{backgroundColor:bg}}>
            <button onMouseEnter ={bgChange}
            onMouseLeave={bgBack}> {name}</button>
        </div>
        </>

    );
};

export default Events;
