import React, { useState } from "react";
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import ListCom from './ListCom';
import '../node_modules/bootstap/dist/css/bootstrap.min.css'


const To =()=>
{
const[item,setItem]=useState("");
const[newitem,setNewItem]=useState([]);

const itemEvent =(event)=>
{
    setItem(event.target.value);
};

const listofItems=()=>
{
    setNewItem((preValue)=>
    {

        return [...preValue,item];
    });
    setItem(" ");
};
    return(
<> 
     <div className="main_div">
         <div className="center_div">
         <br/>
         <h1>ToDo List</h1>
         <br/>
         <input type="text"value={item} placeholder="Add an Item" onChange={itemEvent}/>
         <Button className="newBtn" onClick={listofItems}><AddIcon/></Button>
         <button className="btn btn-success">J</button>
         <br/>
         <ol>
       
         {newitem.map((val,index)=>
         {
             return  <ListCom key={index} text={val}/>
         })}
         </ol>



         </div>
     </div>
</>

    );
}

export default To;
